import { useState } from 'react';
import './App.scss';
import TableArea from './component/TableArea/TableArea';
import Head from './component/Head/Head';
import { ThemeContext, MyCouter } from './Contexts.js';

function App() {
  const theme = 'dark';
  const [counter, setCounter] = useState(10);

  function changeContext() {
    setCounter(prevCounter => prevCounter + 1);
    console.log('newCounter', counter);
  }

  return (
    <ThemeContext.Provider value={theme}>
      <MyCouter.Provider value={counter}>
        <div className="flex-container">
          <Head />
          <h1>Hello world</h1>

          //<TableArea />

          <button className="buy-button" onClick={changeContext}>Plus</button>
        </div>
        
      </MyCouter.Provider>
    </ThemeContext.Provider>
    
  );
}

export default App;
